package com.tictactoe;

import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;

import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpSession;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.List;
import java.util.Map;

public class SomeServlet extends HttpServlet {
	private String text = "SKIBIDIDOP";
	private String imgURL = "data:image/jpeg;base64,";

	public void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException {
		response.setContentType("text/html");

		PrintWriter out = response.getWriter();
		out.println("<html><head>");
		out.println(" <link rel=\"stylesheet\" href=\"" +  request.getContextPath() + "/styles/style.css\">");
		out.println("</head>");
		out.println("<body>");
		out.println("<h1>" + "MyFirstServlet!" + "</h1>");
		out.println("<p>" + text + " <p>");
		out.println("<img src=\"" + imgURL + "\" alt=\"slomalsaIMGblin\">");
		out.println("</body></html>");
	}
}
